CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20251011142837_InitialCreate') THEN
    CREATE TABLE "Addresses" (
        "Id" bigint GENERATED BY DEFAULT AS IDENTITY,
        "Street" character varying(100) NOT NULL,
        "Number" character varying(10),
        "Complement" character varying(100),
        "Neighborhood" character varying(100) NOT NULL,
        "City" character varying(100) NOT NULL,
        "State" character varying(100) NOT NULL,
        "PostalCode" character varying(9) NOT NULL,
        CONSTRAINT "PK_Addresses" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20251011142837_InitialCreate') THEN
    CREATE TABLE "Users" (
        "Id" bigint GENERATED BY DEFAULT AS IDENTITY,
        "CPF" character varying(11),
        "CNPJ" character varying(14),
        "Email" text NOT NULL,
        "Password" text NOT NULL,
        "Name" text NOT NULL,
        "Role" text NOT NULL,
        "PersonalAddressId" bigint,
        "CreatedAt" timestamp with time zone NOT NULL,
        CONSTRAINT "PK_Users" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_Users_Addresses_PersonalAddressId" FOREIGN KEY ("PersonalAddressId") REFERENCES "Addresses" ("Id") ON DELETE SET NULL
    );
    END IF;
END $EF$;

DO $EF$
