    CREATE TABLE "Providers" (
        "Id" bigint GENERATED BY DEFAULT AS IDENTITY,
        "UserId" bigint NOT NULL,
        "Bio" character varying(500) NOT NULL,
        "Skills" text[] NOT NULL,
        "WorkAddressId" bigint,
        "PriceBase" real,
        CONSTRAINT "PK_Providers" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_Providers_Addresses_WorkAddressId" FOREIGN KEY ("WorkAddressId") REFERENCES "Addresses" ("Id") ON DELETE SET NULL,
        CONSTRAINT "FK_Providers_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
    );
    END IF;
END $EF$;

